window.validateAriaEmail = function(elem) {
	var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    if (re.test(elem.value)) {
		elem.setAttribute('aria-invalid', false);
	} else {
		elem.setAttribute('aria-invalid', true);
	}
}

