function mayAlsoLikeSlotClick(e, slot) {
	e.preventDefault();
	let xmlhttp = new XMLHttpRequest();
	let trueAnchorElem = mokaFindParentAnchor(e.target);
	xmlhttp._dest_link = trueAnchorElem.href;
	xmlhttp.addEventListener('load', function() {
		self.location = this._dest_link;
	});
	xmlhttp.addEventListener('error', function() {
		self.location = this._dest_link;
	});
	xmlhttp.open("GET", 'https://px1.lifesavvy.com/mal/' + slot, true);
	xmlhttp.send();
}

(function() {
	let slotsVisible = [false, false, false, false, false, false, false, false];
	let sidebarSegmentBecameVisibleCheck = function() {
		for (let x = 1; x <= 8; x++) {
			let el = document.getElementById('sidebar_slot' + x);
			var rect = el.getBoundingClientRect();

			if (rect.bottom > 0 &&
				rect.right > 0 &&
				rect.left < (window.innerWidth || document.documentElement.clientWidth) &&
				rect.top < (window.innerHeight || document.documentElement.clientHeight)
			) {
				if (!slotsVisible[x]) {
					slotsVisible[x] = true;
					let xmlhttp = new XMLHttpRequest();
					xmlhttp.open("GET", 'https://px1.lifesavvy.com/mai/' + x, true);
					xmlhttp.send();
				}
			}
		}
	}
	addEventListener('DOMContentLoaded', sidebarSegmentBecameVisibleCheck, false);
	addEventListener('load', sidebarSegmentBecameVisibleCheck, false);
	addEventListener('scroll', sidebarSegmentBecameVisibleCheck, false);
	addEventListener('resize', sidebarSegmentBecameVisibleCheck, false);
})();

