var IEVersion = function() {
  if (navigator.appName === 'Microsoft Internet Explorer') {
    var re  = new RegExp("MSIE ([0-9]{1,}[.0-9]{0,})");
    if (re.exec(navigator.userAgent) != null) {
      return parseFloat(RegExp.$1);
    }
  }
  return -1;
};

