let mokaFindParentAnchor = function(elem) {
    if (elem.href) return elem;
    if (elem.parentElement) return mokaFindParentAnchor(elem.parentElement);
    return null;
}
var trackonomicsXidSet = function() {
	// Use function context as a static
	this.lastLinkId = this.lastLinkId || 0;
	var links = document.querySelectorAll('a');

	for (var i = 0; i < links.length; i++) {
		var link = links[i].getAttribute('href');
		if (!link) {
			// Mostly autotoc anchors etc.
			continue;
		}
		link = link.replace('%7Bxid%7D', '{xid}');

		if (link && link.indexOf && (link.indexOf('{xid}') != -1 || link.indexOf('amazon.com') != -1)) {
			var letter3 = this.lastLinkId % 26;
			var letter2 = Math.floor(this.lastLinkId / 26) % 26;
			var letter1 = Math.floor(this.lastLinkId / 676);
			var stringLinkId = String.fromCharCode('a'.charCodeAt(0) + letter1) + String.fromCharCode('a'.charCodeAt(0) + letter2) + String.fromCharCode('a'.charCodeAt(0) + letter3);

			links[i]._trxXid = 'fr' + Math.round(Date.now()/1000) + stringLinkId;
			var updatedLink = link.replace('{xid}', links[i]._trxXid);
			var trxnet = updatedLink.match(/\&___trxnet=([a-z]*)/);
			if (trxnet && trxnet.length == 2) {
				links[i].setAttribute('data-trxnetwork', trxnet[1]);
				updatedLink = updatedLink.replace(trxnet[0], '');
			} else if (link.indexOf('amazon.com') != -1) {
				links[i].setAttribute('data-trxnetwork', 'az');
			}
			links[i].setAttribute('href', updatedLink);
			this.lastLinkId++;

