let handleHtgHash = function() {
	if (document.location.hash.indexOf('#htg=') != -1) {
		let htgid = document.location.hash.match(/htg=([0-9]*)/);
		let uid = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, (c) => {
			var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
			return v.toString(16);
		});
		if (htgid.length == 2) {
			document.cookie = 'htg=' + htgid[1] + '; expires=Mon, 15 Jan 2024 00:00:00 UTC; path=/';
			document.cookie = 'htgreader=' + uid + '; expires=Mon, 15 Jan 2024 00:00:00 UTC; path=/';
			document.cookie = 'htg_newsletter_subform_hide=1; expires=Mon, 15 Jan 2024 00:00:00 UTC; path=/';
		}

		history.pushState('', document.title, window.location.pathname);
	}
