	// Load all lazy-loaded images before someone prints:
	let loadAllLazy = () => {
		var imgs = document.querySelectorAll('[data-pagespeed-lazy-src]');
		imgs.forEach(e => e.src = e.attributes['data-pagespeed-lazy-src'].value);
	}
	// IE and FireFox:
	window.onbeforeprint = loadAllLazy;
	// Chrome and Safari:
	let mediaQueryList = window.matchMedia('print');
	mediaQueryList.addListener((mql) => {
    	if (mql.matches) loadAllLazy();
	});

(function() {
	if (document.referrer) {
		var x = document.getElementsByName('moka-pagerefer');
		for (var i = 0; i < x.length; i++) {
			x[i].value = document.referrer;
		}
	}

	document.querySelectorAll('.menu-item, #topmenu li a, .explore-menu-trigger').forEach(function(item) {
		item.addEventListener('click', function(e) {
			var menuLabel = this.innerText.replace('»', '').trim();
			gtag('event', 'Menu Item', {
			  event_catgory: menuLabel,
			  event_label: menuLabel
			});
		}, false);
	});
})();
